import React, { useState, useEffect } from 'react';
import VerticalLink from './VerticalLink';
import './VerticalLinkList.css'; // Si deseas estilos específicos para la lista

export default function VerticalLinkList() {
    // Estado para almacenar el correo ofuscado y ensamblarlo dinámicamente
    const [emailLink, setEmailLink] = useState('');

    // UseEffect para ensamblar el correo cuando el componente se monte
    useEffect(() => {
        const emailUsername = "ariom0910";
        const emailDomain = "gmail.com";
        const emailHref = `mailto:${emailUsername}@${emailDomain}`;
        setEmailLink(emailHref);
    }, []);
    const socialMediaLinks = [
        { 
            image: 'ruta/a/imagen/linkedin.png', 
            title: 'LinkedIn', 
            url: 'https://www.linkedin.com/in/tu-perfil' 
        },
        { 
            image: 'ruta/a/imagen/github.png', 
            title: 'GitHub', 
            url: 'https://github.com/ariannysOronoz' 
        },
        { 
            image: 'ruta/a/imagen/twitter.png', 
            title: 'Email', 
            url: emailLink  
        },
    ];
    return (
        <div className="socialMediaLinks">
            {socialMediaLinks.map((link, index) => (
                <a 
                href={link.url} 
                key={index} 
                target={link.url.startsWith("mailto:") ? "_self" : "_blank"} 
                rel="noopener noreferrer">
                    <VerticalLink 
                    image={link.image} 
                    title={link.title} 
                    />
                </a>
            ))}
        </div>
    );
}
